import React from "react";

import { SpinnerContainer, SpinnerOverlay } from "./with-spinner.style";

const WithSpinner = (wrappedComponents) => ({ isLoading, ...otherProps }) => {
  return isLoading ? (
    <SpinnerOverlay>
      <SpinnerContainer />
    </SpinnerOverlay>
  ) : (
    <wrappedComponents {...otherProps} />
  );
};

export default WithSpinner;
